script:
frappe.ui.form.on('Blanket Order', {
    custom_party_address: function(frm) {
        if (frm.doc.custom_party_address) {
            frappe.call({
                method: "alpha_customization.alpha_customization.blanket_order_address.get_display_address_for_blanket_order",
                args: {
                    address_id: frm.doc.custom_party_address
                },
                callback: function(r) {
                    if (r.message) {

                        frm.doc.custom_party_address_display = r.message; // Assign m to custom_party_address_display field
                        frm.refresh_field('custom_party_address_display'); // Refresh the field to update its value on the form
                    }
                }
            });
        }
    },
});



function method_call_method(frm, party, party_type) {
    frappe.call({
        method: "alpha_customization.alpha_customization.blanket_order_address.get_address_for_blanket_order",
        args: {
            party: party,
            party_type: party_type

        },
        callback: function(r) {
            if (r.message) {
                var k = r.message[0];
                var m = r.message[1];

                frm.doc.custom_party_address = k; // Assign k to custom_party_address field
                frm.doc.custom_party_address_display = r.message[1]; // Assign m to custom_party_address_display field
                frm.refresh_field('custom_party_address'); // Refresh the field to update its value on the form
                frm.refresh_field('custom_party_address_display'); // Refresh the field to update its value on the form


            }
        }
    });
}


frappe.ui.form.on('Blanket Order', {
    supplier: function(frm) {
        if (frm.doc.supplier) {

            frm.set_query("custom_party_address", function() { // Replace with the name of the link field
                return {
                    filters: [
                        ["Dynamic Link", "link_doctype", "=", 'Supplier'],
                        ["Dynamic Link", "link_name", "=", frm.doc.supplier],
                    ]
                };
            });
            method_call_method(frm, frm.doc.supplier, 'Supplier');
        }
    },
});

frappe.ui.form.on('Blanket Order', {
    customer: function(frm) {
        if (frm.doc.customer) {
            frm.set_query("custom_party_address", function() { // Replace with the name of the link field
                return {
                    filters: [
                        ["Dynamic Link", "link_doctype", "=", 'Customer'],
                        ["Dynamic Link", "link_name", "=", frm.doc.customer],
                    ]
                };
            });
            method_call_method(frm, frm.doc.customer, 'Customer');
        }
    },
});



frappe.ui.form.on('Blanket Order Item', {
    item_code: function(frm, cdt, cdn) {
        var child = locals[cdt][cdn];
        var parent = frm.doc;
        if (child.item_code) {
            frappe.call({
                method: "alpha_customization.alpha_customization.blanket_order_address.ItemWeight",
                args: {
                    item_code: child.item_code
                },
                callback: function(r) {

                    var weight = r.message;
                    child.custom_weight = weight;

                }
            });
        }
    }
});

frappe.ui.form.on('Bank Clearance', {
    get_payment_entries: function(frm, cdt, cdn) {

        frm.doc.payment_entries.forEach(function(item) {

            frappe.call({
                method: "alpha_customization.alpha_customization.blanket_order_address.get_name_of_party",
                args: {
                    party: item.against_account,

                },
                callback: function(r) {
                    if (r.message) {
                        var k = r.message;
                        item.custom_against_account_name = k;
                        item.refresh_field('custom_against_account_name');
                        frm.refresh_field('payment_entries');

                    }
                }

            });

        });
        frm.refresh_field('payment_entries');

    }
});

